import React from 'react';
import { useState } from 'react';

const Form = ({ addStudent }) => {
  const [fullname, setFullname] = useState('');
  const [birthDate, setBirthDate] = useState('');
  const [gender, setGender] = useState('');
  const [programStudy, setProgramStudy] = useState('');
  const [faculty, setFaculty] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    const newStudent = {
      fullname,
      birthDate,
      gender,
      faculty,
      programStudy,
    };

    addStudent(newStudent);

    setFullname('');
    setBirthDate('');
    setGender('');
    setProgramStudy('');
    setFaculty('');
  };

  const getFacultyByProgramStudy = (programStudy) => {
    switch (programStudy) {
      case 'Ekonomi':
      case 'Manajemen':
      case 'Akuntansi':
        return 'Fakultas Ekonomi';
      case 'Administrasi Publik':
      case 'Administrasi Bisnis':
      case 'Hubungan Internasional':
        return 'Fakultas Ilmu Sosial dan Politik';
      case 'Teknik Sipil':
      case 'Arsitektur':
        return 'Fakultas Teknik';
      case 'Matematika':
      case 'Fisika':
      case 'Informatika':
        return 'Fakultas Teknologi Informasi dan Sains';
      default:
        return '';
    }
  };

  const handleProgramStudyChange = (e) => {
    const selectedProgramStudy = e.target.value;
    const selectedFaculty = getFacultyByProgramStudy(selectedProgramStudy);
    setProgramStudy(selectedProgramStudy);
    setFaculty(selectedFaculty);
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Fullname
        <input
          type="text"
          value={fullname}
          onChange={(e) => setFullname(e.target.value)}
          data-testid="name"
        />
      </label>
      <br />
      <label>
        Birth Date
        <input
          type="date"
          value={birthDate}
          onChange={(e) => setBirthDate(e.target.value)}
          data-testid="date"
        />
      </label>
      <br />
      <label>
        Gender
        <select
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          data-testid="gender"
        >
          <option value="">Select Gender</option>
          <option value="Male">Male</option>
          <option value="Female">Female</option>
          <option value="Other">Other</option>
        </select>
      </label>
      <br />
      <label>
        Program Study
        <input
          type="text"
          value={programStudy}
          onChange={handleProgramStudyChange}
          data-testid="prody"
        />
      </label>
      <br />
      <label>
        Faculty
        <select
          value={faculty}
          onChange={(e) => setFaculty(e.target.value)}
          data-testid="faculty"
        >
          <option value="">Select Faculty</option>
          <option value="Fakultas Ekonomi">Fakultas Ekonomi</option>
          <option value="Fakultas Ilmu Sosial dan Politik">Fakultas Ilmu Sosial dan Politik</option>
          <option value="Fakultas Teknik">Fakultas Teknik</option>
          <option value="Fakultas Teknologi Informasi dan Sains">Fakultas Teknologi Informasi dan Sains</option>
        </select>
      </label>
      <br />
      <button type="submit" data-testid="submit">
        Add Student
      </button>
    </form>
  );
};

export default Form;
