import React from 'react';
import { useState } from "react";

const Form = ({ addStudent }) => {
  const [fullname, setFullname] = useState("");
  const [birthDate, setBirthDate] = useState("");
  const [gender, setGender] = useState("");
  const [programStudy, setProgramStudy] = useState("");
  const [faculty, setFaculty] = useState(""); // Menambahkan state untuk fakultas

  const handleSubmit = (e) => {
    e.preventDefault();

    // Menggunakan fungsi getFacultyByProgramStudy untuk mendapatkan fakultas
    const newFaculty = getFacultyByProgramStudy(programStudy);

    const newStudent = {
      fullname,
      birthDate,
      gender,
      faculty: newFaculty,
      programStudy,
    };

    addStudent(newStudent);

    setFullname("");
    setBirthDate("");
    setGender("");
    setProgramStudy("");
    setFaculty(""); // Mengatur kembali fakultas menjadi kosong setelah submit
  };

  const getFacultyByProgramStudy = (programStudy) => {
    switch (programStudy) {
      case "Ekonomi":
      case "Manajemen":
      case "Akuntansi":
        return "Fakultas Ekonomi";
      case "Administrasi Publik":
      case "Administrasi Bisnis":
      case "Hubungan Internasional":
        return "Fakultas Ilmu Sosial dan Politik";
      case "Teknik Sipil":
      case "Arsitektur":
        return "Fakultas Teknik";
      case "Matematika":
      case "Fisika":
      case "Informatika":
        return "Fakultas Teknologi Informasi dan Sains";
      default:
        return "";
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Fullname
        <input
          type="text"
          value={fullname}
          onChange={(e) => setFullname(e.target.value)}
          data-testid="name"
        />
      </label>
      <br />
      <label>
        Birth Date
        <input
          type="text"
          value={birthDate}
          onChange={(e) => setBirthDate(e.target.value)}
          data-testid="date"
        />
      </label>
      <br />
      <label>
        Gender
        <input
          type="text"
          value={gender}
          onChange={(e) => setGender(e.target.value)}
          data-testid="gender"
        />
      </label>
      <br />
      <label>
        Program Study
        <input
          type="text"
          value={programStudy}
          onChange={(e) => setProgramStudy(e.target.value)}
          data-testid="prody"
        />
      </label>
      <br />
      <label>
        Faculty {/* Menambahkan label dan input field untuk fakultas */}
        <input
          type="text"
          value={faculty}
          onChange={(e) => setFaculty(e.target.value)}
          data-testid="faculty"
        />
      </label>
      <br />
      <button type="submit" data-testid="submit">
        Add Student
      </button>
    </form>
  );
};

export default Form;
